import React from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';

const PersonCard = ({ person }) => {
  if (!person) return null;

  return (
    <View style={styles.card}>
      {/* Profile Image */}
      {person.photo_url ? (
        <Image source={{ uri: person.photo_url }} style={styles.image} />
      ) : (
        <View style={[styles.image, styles.noPhoto]}>
          <Text>No photo</Text>
        </View>
      )}

      {/* Name + Age */}
      <Text style={styles.name}>
        {person.name || 'Unknown'} {person.age ? `, ${person.age}` : ''}
      </Text>

      {/* Location */}
      <Text style={styles.location}>
        {person.location || ''}
      </Text>
    </View>
  );
};

export default PersonCard;

const styles = StyleSheet.create({
  card: {
    backgroundColor: '#fff',
    borderRadius: 12,
    padding: 16,
    alignItems: 'center',
    elevation: 4, // Android shadow
    shadowColor: '#000', // iOS shadow
    shadowOpacity: 0.2,
    shadowRadius: 6,
    shadowOffset: { width: 0, height: 3 },
  },
  image: {
    width: 280,
    height: 350,
    borderRadius: 12,
    marginBottom: 12,
    resizeMode: 'cover',
  },
  noPhoto: {
    backgroundColor: '#eee',
    justifyContent: 'center',
    alignItems: 'center',
  },
  name: {
    fontSize: 22,
    fontWeight: '700',
    marginBottom: 4,
  },
  location: {
    fontSize: 16,
    color: '#666',
  },
});
