// PersonCard.js
import React from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';

const PersonCard = ({ person }) => {
  if (!person) return null;

  return (
    <View style={styles.card}>
      {person.photo_url ? (
        <Image source={{ uri: person.photo_url }} style={styles.image} />
      ) : (
        <View style={[styles.image, styles.noPhoto]}>
          <Text>No photo</Text>
        </View>
      )}
      <Text style={styles.name}>
        {person.name} {person.age ? `, ${person.age}` : ''}
      </Text>
      <Text style={styles.location}>
        {person.location || ''}
      </Text>
    </View>
  );
};

export default PersonCard;

const styles = StyleSheet.create({
  card: {
    backgroundColor: '#fff',
    borderRadius: 12,
    padding: 16,
    alignItems: 'center',
  },
  image: {
    width: 250,
    height: 300,
    borderRadius: 10,
    marginBottom: 12,
  },
  noPhoto: {
    backgroundColor: '#eee',
    justifyContent: 'center',
    alignItems: 'center',
  },
  name: {
    fontSize: 20,
    fontWeight: '600',
    marginBottom: 4,
  },
  location: {
    fontSize: 16,
    color: '#666',
  },
});
