import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import Avatar from '../atoms/Avatar';



const styles = StyleSheet.create({
  card: { width: 300, alignSelf:'center', alignItems:'center', backgroundColor:'#fff', borderRadius:12, overflow:'hidden', elevation:4, marginBottom:10 },
  placeholder: { width:300, height:200, alignItems:'center', justifyContent:'center', backgroundColor:'#ddd' },
  footer: { padding:12, width:'100%' },
  name: { fontSize:18, fontWeight:'700' },
  location: { color:'#666', marginTop:4 }
});
