import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import Avatar from '../atoms/Avatar';

export default function PersonCard({ person }) {
    const photo = person.photo?.url || person.photo?.path || null;
  console.log(photo);
  return (
    <View style={styles.card}>
      {photo ? <Avatar uri={photo} size={320} /> : <View style={styles.placeholder}><Text>No photo</Text></View>}
      <View style={styles.footer}>
        <Text style={styles.name}>{person.name}, <Text style={{fontWeight:'600'}}>{person.age}</Text></Text>
        <Text style={styles.location}>{person.latitude ? `${person.latitude.toFixed(2)}, ${person.longitude.toFixed(2)}` : ''}</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  card: { width: 340, alignSelf:'center', alignItems:'center', backgroundColor:'#fff', borderRadius:12, overflow:'hidden', elevation:4, marginBottom:20 },
  placeholder: { width:320, height:320, alignItems:'center', justifyContent:'center', backgroundColor:'#ddd' },
  footer: { padding:12, width:'100%' },
  name: { fontSize:18, fontWeight:'700' },
  location: { color:'#666', marginTop:4 }
});
